            <div class="data-control-id-171385 bd-horizontalmenu-70 clearfix">
                <div class="bd-container-inner">

                    <ul class="data-control-id-171386 bd-menu-63 nav nav-pills navbar-left">
                        @{
                            var pageParents = page.parents ? page.parents : [];
                            var filterItems = function (items) {
                                return items.filter(function (i) {
                                    return i.showInHMenu;
                                });
                            };

                            var inParents = function (id) {
                                return pageParents.some(function (i) {
                                    return i.id === id;
                                });
                            };
                        }

                        @function subMenuBefore(level, hasMegamenu) {
                            <div class="bd-menu-39-popup @if (hasMegamenu && level === 3) { bd-megamenu-popup }">
                            @if (hasMegamenu && level === 2) {
                                <div class="bd-menu-39 bd-mega-grid data-control-id-2149213 bd-grid-60">
                                    <div class="container-fluid">
                                        <div class="separated-grid row">
                            } else {
                                <ul class="data-control-id-171405 bd-menu-39">
                            }
                        }

                        @function subMenuAfter(level, hasMegamenu) {
                            @if (hasMegamenu && level === 2) {
                                        </div>
                                    </div>
                                </div>
                            } else {
                                </ul>
                            }
                            </div>
                        }

                        @function subMenuItemBefore(item, level, hasMegamenu) {
                            @{
                                var additionalClass = 'bd-sub-item';
                                if (hasMegamenu) {
                                    if (level === 2) {
                                        var responsive = {
                                            lg: item.columns_lg || '',
                                            md: item.columns_md || '',
                                            sm: item.columns_sm || '',
                                            xs: item.columns_xs || ''
                                        };

                                        var responsiveClasses = '',
                                            lastMode = '';

                                        for (var mode in responsive) {
                                            if ('xs' === mode) break;
                                            lastMode = mode;
                                            var value = responsive[mode];
                                            if (value) {
                                                responsiveClasses += ' col-' + mode + '-' + Math.round(12 / value);
                                            }
                                        }

                                        if (!responsiveClasses && lastMode) {
                                            for (var i = item.children.length; i <= 12; i++) {
                                                if (i && (12 % i === 0)) {
                                                    responsiveClasses += ' col-' + lastMode + '-' + Math.round(12 / i);
                                                    break;
                                                }
                                            }
                                        }

                                        additionalClass += ' bd-mega-item data-control-id-2149203 bd-menuitem-52';
                                        additionalClass += ' separated-item-43 ' + responsiveClasses;
                                    } else if (level === 3) {
                                        additionalClass += ' bd-mega-item data-control-id-2149227 bd-menuitem-56';
                                    }
                                }
                            }
                            @if (hasMegamenu && level === 2) {
                                <div class="data-control-id-171406 bd-menuitem-38 @(additionalClass)">
                                    <div class="data-control-id-2149215 bd-griditem-43 bd-grid-item">
                            } else {
                                <li class="data-control-id-171406 bd-menuitem-38 bd-sub-item @(additionalClass)">
                            }
                        }

                        @function subMenuItemAfter(level, hasMegamenu) {
                            @if (hasMegamenu && level === 2) {
                                    </div>
                                </div>
                            } else {
                                </li>
                            }
                        }

                        @function renderMenuItem(item) {
                            @{
                                var isActive = inParents(item.id) || page.id === item.id,
                                    itemHref = item.customUrl || (pathToRoot + item.filePath);
                            }
                            <a @if (isActive) { class="active" } title="@(item.titleInBrowser)" href="@(itemHref)" @if (item.target) { target="@(item.target)" }>@(item.caption)</a>
                        }

                        @function renderHMenuLevels(item, level, hasMegamenu) {
                            @{ var items = filterItems(item.children); }
                            @if (items.length) {

                                @(subMenuBefore(level, hasMegamenu))

                                @for (var i = 0; i < items.length; i++) {
                                    @{ var subItem = items[i]; }

                                    @(subMenuItemBefore(item, level, hasMegamenu))

                                    @(renderMenuItem(subItem))

                                    @if (item.children) {
                                        @(renderHMenuLevels(subItem, level + 1, hasMegamenu))
                                    }

                                    @(subMenuItemAfter(level, hasMegamenu))
                                }

                                @(subMenuAfter(level, hasMegamenu))
                            }
                        }

                        @{ var items = filterItems(pages); }
                        @for (var i = 0; i < items.length; i++) {
                            @{
                                var levels = 'one level';
                                var item = items[i];

                                var hasMegamenu = levels === 'megamenu' && !!item.children && item.children.some(function (child) {
                                    return child.children && child.children.length && !item.megamenu;
                                });
                            }
                            <li class="data-control-id-171387 bd-menuitem-36
                                        @if (item.children && filterItems(item.children).length) { bd-submenu-icon-only }
                                        bd-toplevel-item
                                        @if (hasMegamenu) {  }
                                        "
                                    @if (hasMegamenu) {
                                        data-mega-width="@(item.megamenuWidth ? item.megamenuWidth : 'sheet')"
                                        data-mega-width-value="@(item.customMenuWidth ? item.customMenuWidth : '600')"
                                    }>

                                @(renderMenuItem(item))

                                @if (item.children) {
                                    @(renderHMenuLevels(item, 2, hasMegamenu))
                                }
                            </li>
                        }
                    </ul>

                </div>
            </div>


            </div>
    </nav>

}
    </div>
</div>

